import React, { useState } from 'react';

import TreeItem from '../TreeItem/TreeItem';

import './TreeList.scss';

const List = ({ children }) => {
  return (
    <div className={`list-container`}>
      {children}
    </div>
  )
}

const TreeList = ({ data }) => {
  const [collapsed, setCollapsed] = useState(false)

  const toggleCollapse = () => setCollapsed((val) => !val)
  const handleCheck = (e) => console.log('check')

  const renderList = (item, i) => {
    const children = Object.values(item.children)

    return (
      <TreeItem 
        key={item.id}
        item={item} 
        index={i} 
        checked={item.checked}
        setChecked={handleCheck}
        collapsed={collapsed}
        toggleCollapse={toggleCollapse}
        children={children}
      />
    )

    if (!!children.length) {
      return (
        <List key={item.id}>
          {children.map(renderList)}
        </List>
      )
    } else {
      return (
        <TreeItem 
          key={item.id}
          item={item} 
          index={i} 
          checked={item.checked}
          setChecked={handleCheck}
          collapsed={collapsed}
          toggleCollapse={toggleCollapse}
        />
      )
    }
  }

  return (
    <div className="app">
      <List>
        {
          data.map(renderList)
        }
      </List>
    </div>
  )
}

export default TreeList;
