import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronUp } from '@fortawesome/free-solid-svg-icons'

import './TreeItem.scss'
import TreeList from '../TreeList/TreeList'

const TreeItem = ({ 
  item, 
  index, 
  checked, 
  setChecked,
  children,
  child,
  isCollapsed
}) => {
  const [collapsed, setCollapsed] = useState(false)

  const toggleCollapse = () => setCollapsed((val) => !val)
  return (
    <div 
      className={`tree-item-container ${child ? 'child-item' : ''} `} 
      onClick={toggleCollapse}
    >
      <div className="item-header">
        <div>
          <input type="checkbox"></input>
          <span>Item {index} {item.name}</span>
        </div>
        {children.length > 0 && <FontAwesomeIcon className={`icon ${collapsed && 'collapsed'}`} icon={faChevronUp} />}
      </div>
      <div className={`item-body ${isCollapsed ? 'collapsed-item' : ''}`}>
        {collapsed && children?.length > 0 && <TreeList data={children} isCollapsed />}
      </div>
    </div>
  )
}

export default TreeItem
