import React, { useState, useEffect } from 'react';
import api from './services/api'

import './App.scss';
import TreeItem from './components/TreeItem/TreeItem';

const List = ({children}) => {
  // console.log('---->', children)
  return (
    <div className="list-container">
      {children}
    </div>
  )
}

const App = () => {
  const [data, setData] = useState([])

  useEffect(() => {
    api.get('/data')
      .then((res) => {
        setData(Object.values(res.data))
      })
      .catch(err => console.log('Error -->', err))
  }, [])

  console.log(data)
  const handleCheck = (e) => console.log('check')

  const renderList = (item, i) => {
    const children = Object.values(item.children)
    console.log(children)
    if (children.length) {
      return (
        <List key={item.id}>
          <span>Filhos</span>
        </List>
      )
    } else {
      return (
        <TreeItem 
          key={item.id}
          item={data[i]} 
          index={i} 
          checked={data[i].checked}
          setChecked={handleCheck}
        />
      )
    }
  }

  return (
    <div className="app">
      {/* {keys.map()} */}
      <List>
        {/* {
          keys.map((d, i) => 
            <TreeItem 
              item={data[i]} 
              index={i} 
              checked={data[i].checked}
              setChecked={handleCheck}
            />
          )
        } */}
        {/* {
          keys.map(renderList)
        } */}
        {
         data.map(renderList)
        }
      </List>
    </div>
  )
}

export default App;
