import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronUp } from '@fortawesome/free-solid-svg-icons'

import TreeList from '../TreeList/TreeList'

import './TreeItem.scss'

const TreeItem = ({ 
  item, 
  index, 
  children,
  child,
  isChecked
}) => {
  const [collapsed, setCollapsed] = useState(false)
  const [checked, setChecked] = useState(isChecked)

  const toggleCollapse = () => setCollapsed((val) => !val)
  const toggleChecked = () => setChecked((val) => !val)

  return (
    <div 
      className={`tree-item-container ${child ? 'child-item' : ''} `} 
    >
      <div className="item-header">
        <div>
          <input type="checkbox" onClick={toggleChecked}></input>
          <span onClick={toggleCollapse}>Item {index} {item.name}</span>
        </div>
        {children.length > 0 && (
          <FontAwesomeIcon onClick={toggleCollapse} className={`icon ${collapsed && 'collapsed'}`} icon={faChevronUp} />
        )}
      </div>
      <div className={`item-body ${collapsed ? 'collapsed-item' : ''}`}>
        {collapsed && children?.length > 0 && (
          <TreeList data={children} checked={checked} isCollapsed  />
        )}
      </div>
    </div>
  )
}

export default TreeItem
