import React, { useState, useEffect } from 'react';
import api from './services/api'

import './App.scss';
import TreeItem from './components/TreeItem/TreeItem';

const List = (children) => {
  return (
    <div className="list-container">
      {children}
    </div>
  )
}

const App = () => {
  const [data, setData] = useState([])
  const [keys, setKeys] = useState([])

  // const getKeys = (obj) => {
  //   Object.keys(obj).map(setKeys)
  // }

  useEffect(() => {
    api.get('/data')
      .then((res) => {
        setData(res.data)
        setKeys(Object.keys(res.data))
      })
      .catch(err => console.log('Error -->', err))
  }, [])

  console.log(data)
  const handleCheck = (e) => console.log('check')

  const renderList = (item, i) => {
    if (item) {
      return (
        <List key={item.id}>
          <span>Filhos</span>
        </List>
      )
    } else {
      return (
        <TreeItem 
          item={data[i]} 
          index={i} 
          checked={data[i].checked}
          setChecked={handleCheck}
        />
      )
    }
  }

  return (
    <div className="app">
      {/* {keys.map()} */}
      <List>
        {/* {
          keys.map((d, i) => 
            <TreeItem 
              item={data[i]} 
              index={i} 
              checked={data[i].checked}
              setChecked={handleCheck}
            />
          )
        } */}
        {
          keys.map(renderList)
        }
      </List>
    </div>
  )
}

export default App;
