import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronUp } from '@fortawesome/free-solid-svg-icons'

import './TreeItem.scss'

const children = [
  {
    name: "teste"
  }
]

const TreeItem = ({ 
  item, 
  index, 
  checked, 
  setChecked,
  children,
  child
}) => {
  const [collapsed, setCollapsed] = useState(false)

  const toggleCollapse = () => setCollapsed((val) => !val)

  const renderChildren = () => children.map(c => (
    <div 
      key={c.id}
      className="tree-item-container" 
      onClick={toggleCollapse}
    >
      <div className="item-header">
        <div>
          <input type="checkbox"></input>
          <span>Item {item.name}</span>
        </div>
        <FontAwesomeIcon className={`icon ${collapsed && 'collapsed'}`} icon={faChevronUp} />
      </div>
      <div className="item-body">
        { collapsed && renderChildren() }
      </div>
    </div>
  ))

  return (
    // <div>
    //   {renderChildren()}
    // </div>
    <div 
      className={`tree-item-container ${child ? 'child-item' : ''}`} 
      onClick={toggleCollapse}
    >
      <div className="item-header">
        <div>
          <input type="checkbox"></input>
          <span>Item {item.name}</span>
        </div>
        <FontAwesomeIcon className={`icon ${collapsed && 'collapsed'}`} icon={faChevronUp} />
      </div>
      <div className="item-body">

      </div>
    </div>
  )
}

export default TreeItem
