import React, { useState, useEffect } from 'react';
import api from './services/api'

import './App.scss';
import TreeItem from './components/TreeItem/TreeItem';

const List = ({ children, child }) => {
  return (
    <div className={`list-container ${child ? 'child' : ''}`}>
      {children}
    </div>
  )
}

const App = () => {
  const [data, setData] = useState([])

  useEffect(() => {
    api.get('/data')
      .then((res) => {
        setData(Object.values(res.data))
      })
      .catch(err => console.log('Error -->', err))
  }, [])

  console.log(data)
  const handleCheck = (e) => console.log('check')

  const renderList = (item, i) => {
    const children = Object.values(item.children)

    if (!!children.length) {
      return (
        <List key={item.id} child>
          {children.map(renderList)}
        </List>
      )
    } else {
      return (
        <TreeItem 
          key={item.id}
          item={item} 
          index={i} 
          checked={item.checked}
          setChecked={handleCheck}
        />
      )
    }
  }

  return (
    <div className="app">
      {/* {keys.map()} */}
      <List>
        {/* {
          keys.map((d, i) => 
            <TreeItem 
              item={data[i]} 
              index={i} 
              checked={data[i].checked}
              setChecked={handleCheck}
            />
          )
        } */}
        {
          data.map(renderList)
        }
      </List>
    </div>
  )
}

export default App;
